package vetores;

public class vetoresInteiros {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numeros[] = {1, 2, 3, 4, 5};

		System.out.println("O tamanho do seu vetor é: " + numeros.length);
		System.out.println("O valor do índice 2 é: " + numeros[numeros.length - 1]);
		System.out.println("O valor do índice 2 é: " + numeros[2]);

	}

}
